import React, { Component } from "react";
import { Upload, Input, Modal, Button, message } from 'antd'
import {  PlusOutlined } from '@ant-design/icons';
import { BsPeopleCircle } from "react-icons/bs";
import { GiSmartphone ,GiModernCity } from "react-icons/gi";
import { HiOutlineMail, HiOutlineLocationMarker } from "react-icons/hi";
import ImgCrop from 'antd-img-crop';
import NavBar from "./NavBar";


function getBase64(file) {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = error => reject(error);
    });
  }

export default class Profile extends Component{ 
    state = {
        about: true,
        edit: false,
        reset: false,
        name: '',
        address: '',
        email: '',
        phone: '',
        zipCode: '',
        password: '',
        newPassword: '',
        fileList: [],
        upFiles: [], 
        previewVisible: false,
        previewImage: '',
        previewTitle: '',
        users: [],
        photos: []
    }

    componentDidMount() {
        fetch("http://localhost:3000/users")
        .then(res => res.json())
        .then(users => this.setState({users}))

        fetch("http://localhost:3000/photos")
        .then(res => res.json())
        .then(photos => this.setState({photos}))
    }

   //change upload value
   onChange = ({ target: { value } }) => {
    this.setState({ value });
  };

  beforeUploadFile = (file) => {
    const {upFiles} = this.state;
    upFiles.push(file);
    this.setState({upFiles: upFiles});

  }

  //handle Onchange of upload
  handleChange = ({ fileList }) => this.setState({ fileList });

  //handle cancel of upload
  handleCancel = () => this.setState({ previewVisible: false });

  //handle preview of uploadalbumName
  handlePreview = async file => {
    if (!file.url && !file.preview) {
      file.preview = await getBase64(file.originFileObj);
    }

    this.setState({
      previewImage: file.url || file.preview,
      previewVisible: true,
      previewTitle: file.name || file.url.substring(file.url.lastIndexOf('/') + 1),
    });
  };

  //handle submit upload
  handleSubmit = () => {
    const {upFiles} = this.state;
    let formData = new FormData();
    upFiles.forEach((file) => {
      formData.append('img', file);
    });
    fetch(`http://localhost:3000/users/${this.props.loginUser.id}`, {
      method: "PATCH",
      body: formData
    })
    .then(res => res.json())
    .then(() => {
        message.success('Photo is uploaded')
    })
    this.setState({
      upFiles: [],
      fileList: []
    })
} 


handleFormSubmit = (e) => {
    e.preventDefault()
    const {name, address, email, phone, zipCode} = this.state
    const updatedUser = {
            name: name,
            address: address,
            email: email,
            phone: phone,
            zipCode: zipCode,
    }

    fetch(`http://localhost:3000/users/${this.props.loginUser.id}`, {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(updatedUser),
    })
    .then(res => res.json())
    .then(() => {
        this.componentDidMount()
    })
} 

    handleAbout = () => {
        this.setState({
            about: true,
            edit: false,
            reset: false
        })
    }

    handleEdit = () => {
        this.setState({
            about: false,
            edit: true,
            reset: false
        })
    }

    handleRest = () => {
        this.setState({
            about: false,
            edit: false,
            reset: true
        })
    }


    handleNameOnchange = (e) => {
        this.setState({
            name: e.target.value
        })
    }

    handleAddressOnchange = (e) => {
        this.setState({
            address: e.target.value
        })
    }

    handleEmailOnchange = (e) => {
        this.setState({
            email: e.target.value
        })
    }

    handlePhoneOnchange = (e) => {
        this.setState({
            phone: e.target.value
        })
    }

    handleZipCodeOnchange = (e) => {
        this.setState({
            zipCode: e.target.value
        })
    }

    handlePassword = (e) => {
        this.setState({
            password: e.target.value
        })
    }

    handleNewPassword = (e) => {
        this.setState({
            newPassword: e.target.value
        })
    }



    render() {
        const phoneformat =  `(${this.props.loginUser.phone.slice(0, 3)}) ${this.props.loginUser.phone.slice(3, 6)}-${this.props.loginUser.phone.slice(6, 10)}`
        const { fileList, previewImage, previewTitle, previewVisible } = this.state
        const userArr = this.state.users.filter(user => user.id === this.props.loginUser.id)
        const userPhoto = this.state.photos.filter(photo => photo.user_id === this.props.loginUser.id)
        console.log(userArr)
        return(
            <div>
                 {
                 Object.keys(this.props.loginUser).length > 0
                 ?<div className="profile-page">
                     <div className="profile-page-header">
                        {userPhoto.map(photo => {
                            const rotate = Math.floor(Math.random() * 40 - 20)
                            return <img
                                    className="profile-page-header-img"
                                    key={photo.id}
                                    style={{transform: `rotate(${rotate}deg)`}}
                                    src={photo.image} 
                                    />
                            })
                        }
                     </div>
                    <NavBar loginUser={this.props.loginUser} handlehideMusic={this.props.handlehideMusic}/>
                    <div className="sub-nav">
                            {userArr.map(user => {
                                if(user.img !== ''){
                                    return <img  className="sub-nav-img" src={user.img} /> 
                                }else{
                                    return <div></div>
                                } 
                                })
                            }
                            <li className="about-btn"  onClick={() => this.handleAbout()}>About</li>
                            <li className="edit" onClick={() => this.handleEdit()}>Edit Profile</li>
                            <li className="reset" onClick={() => this.handleRest()}>Change Password</li>  
                    </div>

                    {this.state.about
                    ?<div className="about">
                        {userArr.map(user => {
                           if(user.img !== ''){
                            return <img src={user.img} className="about-img" /> 
                           }else{
                            return <img src='https://tanzolymp.com/images/default-non-user-no-photo-1.jpg' /> 
                           } 
                        })
                        }
                        <div className="about-img-div"></div>
                        <div className="about-img-div-div"></div>
                        <div className="about-img-div-div-div"></div>
                            <div className="about-name" ><BsPeopleCircle style={{fontSize: '40', marginBottom: "5"}}/> { this.props.loginUser.name.toUpperCase() }</div>
                        <div className="about-icon">
                            <div className="about-phone" > <GiSmartphone style={{fontSize: '24', marginBottom: "5"}} /> Phone: { phoneformat }</div>
                            <div className="about-email"> <HiOutlineMail style={{fontSize: '24', marginBottom: "5", marginRight: '5px'}} />Email: { this.props.loginUser.email.toUpperCase() }</div>
                            <div className="about-add"> <HiOutlineLocationMarker style={{fontSize: '24', marginBottom: "5", marginRight: '5px'}} />Address: { this.props.loginUser.address.toUpperCase() }</div>
                            <div className="about-zip"> <GiModernCity style={{fontSize: '24', marginBottom: "5", marginRight: '5px'}} /> Zip Code: { this.props.loginUser.post_code }</div>
                        </div>
                    </div>
                    :null
                    }
                    {this.state.edit
                        ?<div className="profile-page-upload">
                                <Upload
                                    listType="picture-card"
                                    fileList={fileList}
                                    beforeUpload={this.beforeUploadFile}
                                    onPreview={this.handlePreview}
                                    onChange={this.handleChange}
                                    multiple={false}
                                >
                                    <PlusOutlined />
                                    <div style={{ marginTop: 8}}>Upload</div>
                                </Upload>
                                <Modal
                                    visible={previewVisible}
                                    title={previewTitle}
                                    footer={null}
                                    onCancel={this.handleCancel}
                                    >
                                    <img
                                        src={previewImage}
                                        alt={previewTitle}
                                        style={{ width: '100%', height: '100%'}}
                                    />
                                </Modal>
                            <Button style={{marginTop: '10px'}} onClick={() => this.handleSubmit()} type='primary' >Upload</Button>
                            <form className="edit" onSubmit={(e) => this.handleFormSubmit(e)}>
                                <Input
                                style={{width: '400px'}}
                                addonBefore="Name: "
                                value={this.state.name}
                                onChange={(e) => this.handleNameOnchange(e)}    
                                >
                                </Input>
                                <Input
                                style={{width: '400px'}}
                                addonBefore="Address: "
                                value={this.state.address}
                                onChange={(e) => this.handleAddressOnchange(e)}
                                >
                                </Input>
                                <Input
                                style={{width: '400px'}}
                                addonBefore="Email: "
                                value={this.state.email}
                                onChange={(e) => this.handleEmailOnchange(e)}
                                >
                                </Input>
                                <Input
                                style={{width: '400px'}}
                                addonBefore="Phone: "
                                value={this.state.phone}
                                onChange={(e) => this.handlePhoneOnchange(e)}
                                >
                                </Input>
                                <Input
                                style={{width: '400px'}}
                                addonBefore="Zip Code: "
                                value={this.state.zipCode}
                                onChange={(e) => this.handleZipCodeOnchange(e)}
                                >
                                </Input>
                                <Button style={{marginTop: '10px'}} onClick={() => this.handleSubmit()} type='primary' >Submit</Button>
                            </form>
                        </div> 
                        :null 
                    }
                    {this.state.reset
                        ? <form>
                                <Input
                                style={{width: '400px'}}
                                addonBefore="New password: "
                                value={this.state.password}
                                onChange={(e) => this.handlePassword(e)}
                                >
                                </Input>
                                <Input
                                style={{width: '400px'}}
                                addonBefore="Confirm Password: "
                                value={this.state.newPassword}
                                onChange={(e) => this.handleNewPassword(e)}
                                >
                                </Input>

                        </form>
                        :null
                    }    
                 </div>
                 :this.props.history.push('/')
                }
            </div>
        )
    }
}
