import React, { Component } from "react";
import Navbar from './NavBar'
import JournalList from './Container/JournalList'

export default class Journal extends Component {

  state = {
    journals: [],
    journalDrafts: [],
  } 

  componentDidMount() {
    fetch("http://localhost:3000/journals")
    .then(res => res.json())
    .then(journals => this.setState({journals}))

    fetch("http://localhost:3000/journal_drafts")
    .then(res => res.json())
    .then(journalDrafts => this.setState({journalDrafts}))

  }


  render() {
    return (
      <div>
        { Object.keys(this.props.loginUser).length > 0 
          ?<div>
            <Navbar />
              <div className="sub navbar">
                  <li>Draft({this.state.journalDrafts.length === 0 ? null : this.state.journalDrafts.length - 1 })</li>
              </div>

            <button onClick={() => this.handleDisplay()}>Write a journal</button>


            <div className="journal">
                {this.state.journals.length !== 0 
                ? this.state.journals.map((journal, index) => {
                  return <JournalList journal={journal} key={index} handleDelete={this.handleDelete}/>})
                :<div>
                  Here is your jourany begin
                </div>
                }
            </div>
          </div>
           :this.props.history.push('/')
        }
      </div>

    );
  }
}
