import React, {Component} from "react";
import { Image } from 'antd'
import Navbar from './NavBar'
import Journal from './Journal'
import Music from './Music'

export default class HomePage extends Component  {

  state = {
    photos: [],
    journals: []
  }

  componentDidMount() {
    fetch("http://localhost:3000/photos")
    .then(res => res.json())
    .then(photos =>{
       if(photos.length > 3) {
         const recentPhoto = photos.slice(Math.max(photos.length - 3), 1)
            this.setState({
              photos: recentPhoto
            })
       }else{
            this.setState({photos})
       }
    })

    fetch("http://localhost:3000/journals")
    .then(res => res.json())
    .then(journals => {
      if(journals.length > 3) {
        const recentJournal = journals.slice(Math.max(journals.length - 3), 1)
          this.setState({
            journals: recentJournal
          })
      }else {
        this.setState({journals})
      } 
    })
  }

  render() {
      console.log(this.state.photos)
    return (
      <div className="home-page">
        {
        Object.keys(this.props.loginUser).length > 0
        ?<div>
          <h1>My <span>Place</span></h1>
          <Navbar loginUser={this.props.loginUser}/>
          {this.state.photos.map(photo => {
            return<div className="photo">
                <Image                
                width={300}
                height={200}
                src={photo.image}>
                </Image>
            </div>
          })
          }

        </div>
        : this.props.history.push('/')
        }
      </div>
      );
    }
  }
