import { DeleteFilled} from '@ant-design/icons';
import { useState } from 'react';
import { render } from '@testing-library/react';

function AlbumList(props) {

    //Show each album cover photo
    const coverPhot = () => {
        const albumPhoto = props.photos.filter(photo => photo.album_id == props.album.id)
        const photosUrl = albumPhoto.map(photo => {
            return photo.image
        })
        return photosUrl[0]
    }

    //Delete album
    handleDelete =(albumObj) => {
        fetch(`http://localhost:3000/photos/${albumObj.id}`, {
            method: "DELETE",
          })
          .then(res => res.json())
          .then(() => {
            this.componentDidMount()
          })
        }

    return (
      <div className="photo-List">
             <img
                width={300}
                height={200}
                src={coverPhot()}
            />

            <DeleteFilled style={{fontSize: "24px"}} />

      </div>

    )
  }

  export default AlbumList;
