import React, { Component } from "react";
import Navbar from './NavBar'
import JournalList from './Container/JournalList'
import { render } from "@testing-library/react";

export default class Journal extends Component {

  state = {
    display: true,
    journals: [],
    journalDrafts: [],
    title: "",
    content: ""
  } 

  // let [display, setDisplay] = useState(true)
  // let [journals, setJournals] = useState([])
  // let [journalDrafts, setJournalDrafts] = useState([])
  // let [title, setTitle] = useState("")
  // let [content, setContent] = useState("")


  handleDisplay = ()=> {
    this.setState({
      display: !this.state.display
    })
  }

  componentDidMount() {
    fetch("http://localhost:3000/journals")
    .then(res => res.json())
    .then(journals => this.setState({journals}))

    fetch("http://localhost:3000/journal_drafts")
    .then(res => res.json())
    .then(journalDrafts => this.setState({journalDrafts}))

  }

  // useEffect(() => {
  //   fetch("http://localhost:3000/journals")
  //   .then(res => res.json())
  //   .then(journals => setJournals(journals))

  //   fetch("http://localhost:3000/journal_drafts")
  //   .then(res => res.json())
  //   .then(journalDrafts => setJournalDrafts(journalDrafts))
  // }, [journals])

   handlePublish = (e) => {
     e.preventDefault()
    const newJouranl = {
      user_id: this.props.loginUser.id,
      title: this.state.title,
      content: this.state.content
    }

    fetch("http://localhost:3000/journals", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newJouranl),
    })
    .then(res => res.json())
    .then(journal => {
      this.setState({
        journals: [...this.state.journals, journal]
      })
    })
  }

   handleDelete = (jouranl) => {
    fetch(`http://localhost:3000/journals/${jouranl.id}`, {
      method: "DELETE"
    })
    .then(res => res.json())
    .then(() => {
      this.setState({
        journals: [...this.state.journals]
      })
      alert("Your journal is being deleted")
    })
  }


  render() {
    return (
      <div>
        { Object.keys(this.props.loginUser).length > 0 
          ?<div>
            <Navbar />
              <div className="sub navbar">
                  <li>Draft({this.state.journalDrafts.length === 0 ? null : this.state.journalDrafts.length - 1 })</li>
              </div>

            <button onClick={() => this.handleDisplay()}>Write a journal</button>

            {this.state.display
            ?<div className="journal">
                {this.state.journals.length !== 0 
                ? this.state.journals.map((journal, index) => {
                  return <JournalList journal={journal} key={index} handleDelete={this.handleDelete}/>})
                :<div>
                  Here is your jourany begin
                </div>
                }
            </div>
            :<div>
                <h2>New Journal</h2>
                  <div className="writing"> 
                      <input className="title"
                          placeholder="Please enter your title"
                          type="text"
                          value = {this.state.title}
                          onChange={(e) => this.setState({title: e.target.value})} />
                      <h2>Content</h2>
                      <textarea className="content"
                          placeholder="Please enter your content"
                          value = {this.state.content}
                          onChange={(e) => this.setState({content: e.target.value})} />
                    <div className="btn">
                        <button onSubmit={(e) => this.handlePublish(e)}>Publish</button>
                        <button>Save as Draft</button>
                    </div>
                  </div>
            </div>}
          </div>
          :  this.props.history.push('/')
        }
      </div>

    );
  }
}
