import React, { Component } from "react";


export default class Sticky extends Component{

    state = {
        userTyping: "",
        stickies: []
    }


    generatorSticky = (text) => {
        const colors = ['#96C2F1', '#BBE1F1', '#E3E197', '#F8B3D0', '#FFCC00']
        const maxWidth = window.innerWidth
        const maxHeight = window.innerHeight
        let randX = parseInt((maxWidth - 250) * Math.random(), 10)
        let randY = parseInt((maxHeight - 350) * Math.random(), 10)
        let background = colors[parseInt(Math.random() * colors.length, 10)]
        let rotate = Math.floor(Math.random() * 20)
        let note = {
            text,
            background,
            top: randY,
            left: randX,
            rotate: rotate
        }
        return note
    }


    addStikcy = (e) => {
        e.preventDefault()
        let newSticky = this.generatorSticky(e.target[0].value)

        this.setState({
            stickies: [...this.state.stickies, newSticky]
        })
        console.log(this.state.stickies)
    }



    handleOnchange = (e) => {
        this.setState({
            userTyping: e.target.value
        })
    }

    render() {
        return(
            <div>
                This is sticky page

                <form onSubmit={(e) => this.addStikcy(e)} className="sticky">   
                    <textarea placeholder="Wirte your memo here!" 
                    value={this.state.userTyping}
                    onChange={(e) => this.handleOnchange(e)}></textarea>
                    <button>Add New</button>
                </form>

                  {this.state.stickies.map(sticky => {
                       return <div className="post" style={{backgroundColor:`${sticky.background}`, top:`${sticky.top}px`, } }>
                        <pre  className="post-text">{sticky.text}</pre> 
                        </div>
                  })}
            </div>
        )
    }
}
