import React, {Component} from "react";


let lastX, lastY, nowX, nowY, minX, minY
export default class Photo3D extends Component {

    handleMouseDown = (e) => {
        lastX = e.clientX
        lastY = e.clientY
     }

     handleOnMouseMove = (e) => {
         nowX = e.clientX
         nowY = e.clientY
         minX = nowX - lastX
         minY = nowY - lastY

         console.log(`minX:${minX}, minY${minY}`)
     } 



    render() {
        const length = this.props.photos.length;
        const deg = 360 / length

        return(
            <div className="view-card-page">
              <CloseCircleOutlined style={{fontSize: '30px'}} onClick={() => this.handleVeiew()}/>
              <div className="view-card" onMouseDown={(e) => this.handleMouseDown(e)} onMouseMove={(e) => this.handleOnMouseMove(e)} style={{transform: `rotateX(${minY}deg) rotateY(${minX}deg)`}}>
              {
                this.state.photos.map((photo, index) => {
                    return<img className="view-card-photo" src={photo.image} style={{transform: `rotateY(${index * deg}deg) translateZ(350px)`, transition: `1s ${(length - 1 - index) * 0.1}s`}}
                    />  
                  })
              }
               </div>
            </div>
        )
    }
}
