import React, { useEffect, useState } from 'react';
import { AiOutlineDoubleLeft, AiOutlineDashboard, AiOutlineCloud } from "react-icons/ai";
import { FiCloudRain } from "react-icons/fi";
import { WiThermometer, WiStrongWind, WiThermometerInternal } from "react-icons/wi";

const api = {
  key: "56f5e6f96b876ed60f2717fd26dd3b1e",
  base: "https://api.openweathermap.org/data/2.5/"
}

function Weather() {
  const [query, setQuery] = useState('');
  const [weather, setWeather] = useState({});
  const [display, setDisplay] = useState(false)


  useEffect(() => {
    fetch(`${api.base}weather?zip=11223&units=metric&APPID=${api.key}`)
        .then(res => res.json())
        .then(result => {
          setWeather(result);
          console.log(result);
        });
  },[]) 

//   const search = evt => {
//     if (evt.key === "Enter") {
//       fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
//         .then(res => res.json())
//         .then(result => {
//           setWeather(result);
//           setQuery('');
//           console.log(result);
//         });
//     }
//   }

  const dateBuilder = (d) => {
    let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();
    return `${day} ${date} ${month} ${year}`
  }

  const handleDisplay = () => {
    setDisplay(!display)
  }

  return (
    <div >
        {/* <div className="search-box">
          <input 
            type="text"
            className="search-bar"
            placeholder="Search..."
            onChange={e => setQuery(e.target.value)}
            value={query}
            onKeyPress={search}
          />
        </div> */}
        {(typeof weather.main != "undefined") ? (
          <div  className="weather-info"
                style={{ height: display ? "380px" : "242.5px" }}
          >
            <div className="cond">
                <div className="cond-logo">
                    <img src={`http://openweathermap.org/img/w/${weather.weather[0].icon}.png`} />
                </div>
                <div className="cond-txt">{weather.weather[0].main}</div>
              </div>
              <div className="tmp">
                <div className="now-tmp">
                  {Math.round((weather.main.temp * 9 / 5) + 32)}
                  <sup>&deg;F</sup>
                </div>
              </div>
              <div
                className="more"
                style={{ transform: display ? "rotate(90deg)" : "rotate(-90deg" }}
              >
                <AiOutlineDoubleLeft onClick={() => handleDisplay()} />
              </div>
              <div className="details">
                    <div  className="detail">
                      <div className="icon"><WiThermometer /></div>
                      <div className="txt">
                        <div className="detail-title">Feel Like</div>
                        <div className="detail-value">{Math.round((weather.main.feels_like * 9 / 5) + 32)}<sup>&deg;F</sup></div>
                      </div>
                    </div>
                    <div  className="detail">
                      <div className="icon"><WiStrongWind /></div>
                      <div className="txt">
                        <div className="detail-title">Wind Speed</div>
                        <div className="detail-value">{`${Math.round(weather.wind.speed)}km/h`}</div>
                      </div>
                    </div>
                    <div  className="detail">
                      <div className="icon"><WiThermometerInternal  /></div>
                      <div className="txt">
                        <div className="detail-title">Humidity</div>
                        <div className="detail-value">{weather.main.humidity}</div>
                      </div>
                    </div>
              </div>

            {/* <div className="cond">
                <div className="cond-logo">
                    <img src={`http://openweathermap.org/img/w/${weather.weather[0].icon}.png`} />

                </div>

            </div> */}

          <div className="location-box">
            <div className="location">{weather.name}, {weather.sys.country}</div>
            <div className="date">{dateBuilder(new Date())}</div>
          </div>
          <div className="weather-box">
            <div className="temp">
              {Math.round(weather.main.temp)}°c
            </div>
            <div className="weather">{weather.weather[0].icon}</div>
            {/* <img src={`http://openweathermap.org/img/w/${weather.weather[0].icon}.png`} /> */}
            {console.log(weather.weather[0])}
          </div>
        </div>
        ) : ('')}
    </div>
  );
}

export default Weather;




