    state = {
        userTyping: "",
        stickies: []
    }


    addStikcy = (e) => {
        e.preventDefault()
        const newSticky = {
            text: this.state.userTyping


        }
    }



    handleOnchange = (e) => {
        this.setState({
            userTyping: e.target.value
        })
    }

    render() {
        return(
            <div>
                This is sticky page

                <form onSubmit={() => this.addStikcy()} className="sticky">   
                    <textarea placeholder="Wirte your memo here!" 
                    value={this.state.userTyping}
                    onChange={(e) => this.handleOnchange(e)}></textarea>
                    <button>Add New</button>
                </form>
            </div>
        )
    }
}
