import { WiCloudDown, WiThermometer, WiStrongWind, WiThermometerInternal, } from "react-icons/wi";

const api = {
    key: "56f5e6f96b876ed60f2717fd26dd3b1e", 
    base: "https://api.openweathermap.org/data/2.5/"
}

export default class Weather extends Component{
        state = {
            input: null,
            weather: {},
            display: false


            // details:[
