            //     {
            //       icon: <AiOutlineEye />,
            //       title: "Visibility",
            //       value: "",
            //     },
            //   ];
        }

        componentDidMount(){
            fetch(`${api.base}weather?zip=11223&units=metric&appid=${api.key} `)
                .then(res=> res.json())
                .then(weather => {
                    this.setState({weather})
                })
        }

         HandleDisplay = () => {
            this.setState({
                display: !this.state.display
            })
          };

    render() {
        const{weather} = this.state
        const innerWeather = weather.weather
        console.log(innerWeather.weather);
        return (
            <div
              className="weather-info"
              style={{ height: this.state.display ? "380px" : "242.5px" }}
            >
              <div className="cond">
                <div className="cond-logo">
                  <img src={weather.icon} alt="" />
                </div>
                <div className="cond-txt">{weather.mian}</div>
              </div>
              <div className="tmp">
                <div className="now-tmp">
                  {/* {tmp} */}
                  <sup>&deg;C</sup>
                </div>
              </div>
              <div
                className="more"
                style={{ transform: this.state.display ? "rotate(90deg)" : "rotate(-90deg" }}
              >
                <AiOutlineDoubleLeft onClick={() => this.HandleDisplay()} />
              </div>
              {/* <div className="details">
                {details.map((item, index) => (
