import React, { Component } from "react";

const sound = 'https://www.soundhelix.com/examples/mp3/SoundHelix-Song-1.mp3';


export default class Music extends Component {

    state = {
      isPause: false,
      musicList: [
        {
          id: "1",
          title: "打上花火",
          info: "DAOKO,米津玄師",
          resource: sound,
          time: "04:50",
          img:
            "http://singerimg.kugou.com/uploadpic/softhead/400/20160913/20160913140233132.jpg"
        },

      ],
      // 当前音乐
      currentMusic: this.state.musicList ? props.musicList[0] : {},
      // 总时间
      totalTime: "00:00",
      // 当前播放时间
      currentTime: "00:00",
      // 进度条item是否可拖动
      processItemMove: false,
      // 进度条item是否可拖动
      volumeProcessItemMove: false,
      // 音量控制显示
      volumeControl: false,
      // 当前的播放模式 1列表循环 2随机 3单曲
      playMode: 1,
      // 歌单显示控制
      isMusicListShow: false
     }

    render() {
      return (
        <div className="music">
            this is Music page
        </div>

      );
    }
  }
