import React from "react";
import { Card, Upload, Modal, Form, Button, Image, Input } from 'antd'
import {  PlusOutlined } from '@ant-design/icons';
import Navbar from './NavBar'
import PhotoList from "./Container/PhotoList";


function getBase64(file) {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = error => reject(error);
  });
}


export default class Photo extends React.Component {

  state = {
    photos: [],
    display: true,
    albumForm: false,
    previewVisible: false,
    previewImage: '',
    previewTitle: '',
    fileList: [], 
    upFiles:[]
    }
    componentDidMount() {
      fetch("http://localhost:3000/photos")
      .then(res => res.json())
      .then(photos => this.setState({photos}))
    }

    // handle diplay photo list
    handleDisplay = () => {
      this.setState({
        display: !this.state.display
      })
    }

    //change upload value
    onChange = ({ target: { value } }) => {
      this.setState({ value });
    };

    //handle Onchange of upload
    handleChange = ({ fileList }) => this.setState({ fileList });

    //handle cancel of upload
    handleCancel = () => this.setState({ previewVisible: false });

    //handle preview of upload
    handlePreview = async file => {
      if (!file.url && !file.preview) {
        file.preview = await getBase64(file.originFileObj);
      }

      this.setState({
        previewImage: file.url || file.preview,
        previewVisible: true,
        previewTitle: file.name || file.url.substring(file.url.lastIndexOf('/') + 1),
      });
    };


    beforeUpload = (file) => {
      const {upFiles} = this.state;
      upFiles.push(file);
      this.setState({upFiles: upFiles});
    }

    //handle submit upload
    handleSubmit = () => {
      const {upFiles} = this.state;
      let formData = new FormData();
       upFiles.forEach((file) => {
        formData.append('image', file);
        formData.append('name', file.name);
         formData.append('user_id', this.props.loginUser.id);
         fetch("http://localhost:3000/photoupload", {
           method: "POST",
           body: formData
         })
         .then(res => res.json())
         .then(() => {
           this.componentDidMount()
         })
        });
      this.setState({
        fileList: [],
        upFiles: []
      })
      this.handleDisplay()
  } 

   //Handle delete photo
   handleDelete =(photoObj) => {
    fetch(`http://localhost:3000/photos/${photoObj.id}`, {
        method: "DELETE",
      })
      .then(res => res.json())
      .then(() => {
        this.componentDidMount()
      })
    }

    handleCreatAblum = () => {
      this.setState({
        albumForm : !this.state.albumForm
      })
    }



    render() {
      const { previewVisible, previewImage, fileList, previewTitle } = this.state;
      console.log(this.state.albumForm);
      return (
      <div className="photo">
        { Object.keys(this.props.loginUser).length > 0 
          ?<div>
            <Navbar />
            <div className="photo-subnav">
                <li>Album</li>
                <li>Photos</li>
            </div>
            <div className="photo-btn">
              <button onClick={() => this.handleDisplay()}>Upload Photo</button>
              <button onClick={() => this.handleCreatAblum()}>Create Album</button>
            </div>
            {this.state.albumForm
            ?<Form>
              <Input addonBefore="Album Title:" style={{width: '500px'}}/>
            </Form>
            :null
          }
            {this.state.display
              ?<div>
                <Image.PreviewGroup>
                {
                this.state.photos.map(photo => {
                    return <PhotoList photo={photo} key={photo.id} handleDelete={this.handleDelete}/>
                  })
                }
                 </Image.PreviewGroup>
              </div>
            :<Form >
            <Card title='Photo Upload'>
                  <Upload
                    listType='picture-card'
                    fileList={fileList}
                    multiple={true}
                    onPreview={this.handlePreview}
                    onChange={this.handleChange}
                    beforeUpload={this.beforeUpload}
                  >
                    <PlusOutlined />
                    <div style={{ marginTop: 8}}>Upload</div>
                  </Upload>
                  <Modal
                      visible={previewVisible}
                      title={previewTitle}
                      footer={null}
                      onCancel={this.handleCancel}
                  >
                    <img
                      src={previewImage}
                      alt={previewTitle}
                      style={{ width: '100%', height: '100%'}}
                    />
                  </Modal>
              <Button onClick={() => this.handleSubmit()} type='primary' >submit</Button>
            </Card>
            </Form>
          } 
          </div>
          :this.props.history.push('/')
        }
      </div>
      )
    }
  }
