import { Image, message, Select  } from 'antd';
import { DeleteFilled, FolderAddFilled, CloseCircleFilled} from '@ant-design/icons';
import { useEffect, useState } from 'react';
import PhotoList from './PhotoList';

const { Option } = Select;
function AlbumDetail(props) {

 const [photos, setPhotos] =useState(props.photos)

 const albumPhoto = photos.filter(photo => photo.album_id == props.album.id)

 const handleRemoveFromAblum =(photoObj) => {
      fetch(`http://localhost:3000/removealbum`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(photoObj)
      })
      .then(res => {
          message.info("Photo removed from curren ablum")
          useEffect(() => {
            fetch("http://localhost:3000/photos")
            .then(res => res.json())
            .then(photos => setPhotos(photos))
          })
      })
    }

    return (
      <div className="photo-List">
            <Image.PreviewGroup>
                {albumPhoto.map(photo => {
                    return <PhotoList photo={photo} handleRemoveFromAblum={handleRemoveFromAblum}/> 
                })
                }
            </Image.PreviewGroup>

      </div>
    );
  }

  export default AlbumDetail;
