import React, {Component} from "react";
import {CloseCircleOutlined } from '@ant-design/icons';

export default class Photo3D extends Component {
    state = {
        lastX: "" , 
        lastY: "" ,
        nowX: "", 
        nowY: "", 
        minX: "", 
        minY: "",
        roX: "",
        roY: "",    
    }

    handleMouseDown = (e) => {
        this.setState({
            lastX: e.clientX,
            lastY: e.clientY
        })
        return false
     }

     handleOnMouseMove = (e) => {
         const {nowX, nowY, lastX, lastY, minX, minY} = this.state
         this.setState({
            nowX: e.clientX,
            nowY: e.clientY,
            minX: nowX - lastX,
            minY: nowY - lastY,
            roX: minX * 0.2,
            roY: minY

         })
         console.log(`minX:${this.state.minX}, minY${this.state.minY}`)
         return false

     } 



    render() {
        const length = this.props.photos.length;
        const deg = 360 / length
        const {minX, minY} = this.state
        return(
            <div className="view-card-page">
              <CloseCircleOutlined style={{fontSize: '30px'}} onClick={() => this.props.handleVeiew()}/>
              <div className="view-card" onMouseDown={(e) => this.handleMouseDown(e)} onMouseOver={(e) => this.handleOnMouseMove(e)} style={{transform: `rotateX(${-minY}deg) rotateY(${minX}deg)` , transition: '10s'}}>
              {
                this.props.photos.map((photo, index) => {
                    return<img className="view-card-photo" src={photo.image} style={{transform: `rotateY(${index * deg}deg) translateZ(350px)`, transition: `1s ${(length - 1 - index) * 0.1}s`}} draggable="false"
                    key={index}  
                    />
                  })
              }
               </div>
            </div>
        )
    }
}
