import React, { Component } from "react";
import Navbar from '../NavBar'


export default class Journal extends Component {

  state = {
    journals: [],
    journalDrafts: [],
    title: "",
    content: ""
  } 

  componentDidMount() {
    fetch("http://localhost:3000/journals")
    .then(res => res.json())
    .then(journals => this.setState({journals}))

    fetch("http://localhost:3000/journal_drafts")
    .then(res => res.json())
    .then(journalDrafts => this.setState({journalDrafts}))

  }

   handlePublish = () => {
    const newJouranl = {
      user_id: this.props.loginUser.id,
      title: this.state.title,
      content: this.state.content
    }

    fetch("http://localhost:3000/journals", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newJouranl),
    })
    .then(res => res.json())
    .then(() => {
        this.props.history.push('/journal')
    })
  }

   handleDelete = (jouranl) => {
    fetch(`http://localhost:3000/journals/${jouranl.id}`, {
      method: "DELETE"
    })
    .then(res => res.json())
    .then(() => {
      this.setState({
        journals: [...this.state.journals]
      })
      alert("Your journal is being deleted")
    })
  }

  handleCancel = () => {
      this.props.history.push('/journal')
  }


  render() {
    return (
      <div>
        { Object.keys(this.props.loginUser).length > 0 
          ?<div>
            <Navbar />
              <div className="sub navbar">
                  <li>Write a Jouranl</li>
                  <li>Draft({this.state.journalDrafts.length === 0 ? null : this.state.journalDrafts.length - 1 })</li>
              </div>
              <div className="writing">
                 <h2>Title</h2>
                <input className="title"
                    placeholder="Please enter your title"
                    type="text"
                    value = {this.state.title}
                    onChange={(e) => this.setState({title: e.target.value})} />
                <h2>Content</h2>
                <textarea className="content"
                    placeholder="Please enter your content"
                    value = {this.state.content}
                    onChange={(e) => this.setState({content: e.target.value})} />
                <div className="btn">
                <button onClick={() => this.handlePublish()}>Publish</button>
                <button>Cancel</button>
                <button>Save as Draft</button>
                </div>
            </div>
          </div>
          :  this.props.history.push('/')
        }
      </div>

    );
  }
}
