import React, { Component } from "react";
import NavBar from "./NavBar";
import { Input } from 'antd';

export default class Profile extends Component{ 
    state = {
        about: true,
        reset: false,
        name: '',
        address: '',
        email: '',
        phone: '',
        zipCode: '',
    }

    handleAbout = () => {
        this.setState({
            about: !this.state.about
        })
    }


    render() {
        console.log(this.props.loginUser)
        console.log(this.state.about)
        const phone =  `(${this.props.loginUser.phone.slice(0, 3)}) ${this.props.loginUser.phone.slice(3, 6)}-${this.props.loginUser.phone.slice(6, 10)}`
        const { name, address, email,} = this.state
        return(
            <div>
                 {
                 Object.keys(this.props.loginUser).length > 0
                 ?<div>
                     <div className="profile-page-header"></div>
                    <NavBar loginUser={this.props.loginUser} handlehideMusic={this.props.handlehideMusic}/>
                    <div className="sub-nav">
                        <ul>
                            <li onClick={() => this.handleAbout()}>About</li>
                            <li onClick={() => this.handleAbout()}>Edit Profile</li>
                            <li>Change Password</li>
                        </ul>
                    </div>

                    {this.state.about
                    ?<div className="about">
                        <div>Name: { this.props.loginUser.name }</div>
                        <div>Phone: { phone }</div>
                        <div>Email: { this.props.loginUser.email }</div>
                        <div>Address: { this.props.loginUser.address }</div>
                        <div>Zip Code: { this.props.loginUser.post_code }</div>
                    </div>
                    :<form className="edit">
                        <Input
                        addonBefore="Name"
                        value={name}
                        >
                        </Input>
                    </form>
                    }    
                 </div>
                 :this.props.history.push('/')
                }
            </div>
        )
    }
}
