import React, { Component } from "react";
import { Select } from 'antd';
import Draggable from 'react-draggable'; 
import { RiDeleteBin6Line } from "react-icons/ri";
import { FaRegEdit } from "react-icons/fa";


const { Option } = Select;
export default class Notes extends Component{

    state = {
        edit: false,
        userTyping: "",
        editTyping: "",
        currentNote: {},
        notes: [],
        colors: '#FFCC00',
        x: "", 
        y: ""
    }

    generatorNote = (text) => {
        const maxWidth = window.innerWidth
        const maxHeight = window.innerHeight
        let randX = parseInt((maxWidth - 250) * Math.random() * 0.4)
        let randY = parseInt((maxHeight - 350) * Math.random() * 0.5)
        let color = this.state.colors
        let note = {
            text,
            color,
            y: randX,
            x: randY
        }
        console.log(randX)
        return note
    }


    addStikcy = (e) => {
        e.preventDefault()
        let newNote = this.generatorNote(e.target[1].value)

        this.setState({
            userTyping: "",
            notes: [...this.state.notes, newNote]
        })
    }

    deleteNote = (noteObj) => {
        const updateStikcyObj = this.state.notes.filter(note => note.id !== noteObj.id )
        this.setState({
            notes: updateStikcyObj
        })
    }

    handleSelectChange = (e) => {
        this.setState({
            colors: e.target.value
        })
    }

    handleOnchange = (e) => {
        this.setState({
            userTyping: e.target.value
        })
    }

    handleEditTypingChange = (e) => {
        this.setState({
            editTyping: e.target.value
        })
    }

    handleEditChange = ( noteObj) => {
        this.setState({
            edit: !this.state.edit,
            currentNote: noteObj,
            editTyping: noteObj.text
        })

        console.log(noteObj)
    }

    handleOnStop = (e, v) => {
        console.log(v)
    }

    render() {
        const {currentNote, editTyping} = this.state

        return(
            <div>
                { Object.keys(this.props.loginUser).length > 0 
                    ?<div className="note-page">
                            <div className="note-page-header"></div>
                            <div className="note-page-container" >
                                <form onSubmit={(e) => this.addStikcy(e)} className="note">
                                    <select defaultValue='Select color' style={{ width: 250, backgroundColor: `${this.state.colors}` }} onChange={this.handleSelectChange}>
                                    <option style={{ width: 200 }} value='Select color' disabled>Select color</option>
                                    <option style={{backgroundColor:`#96C2F1` }} value='#96C2F1'>Blue</option>
                                    <option style={{backgroundColor:`#D5F1BB` }} value='#D5F1BB'>Green</option>
                                    <option style={{backgroundColor:`#E3E197` }} value='#E3E197'>Yellow</option>
                                    <option style={{backgroundColor:`#FFCC00` }} value='#FFCC00'>Orange</option>
                                    </select> 

                                    <textarea placeholder="Wirte your memo here!"
                                    style={{backgroundColor: `${this.state.colors}`}} 
                                    value={this.state.userTyping}
                                    onChange={(e) => this.handleOnchange(e)}></textarea>
                                    <button style={{backgroundColor:`${this.state.colors}`}}>Add New</button>
                                </form>

                                {this.state.notes.map(note => {
                                    return <Draggable 
                                            cancel=".btn"
                                                onStop={this.handleOnStop}
                                                > 
                                                <div className= "post" 
                                                style={{backgroundColor:`${note.color}`, top:`${note.y}px`,
                                                left: `${note.x}px`}}
                                                key={note.id} 
                                                    >     
                                                    <div className="btn" style={{fontSize:'24px', color:'white', textAlign:'right', paddingBottom: '20px'}}>     
                                                        <RiDeleteBin6Line className="btn-cancel"  onClick={() => this.deleteNote(note)} />
                                                        <FaRegEdit className="btn-edit"  onClick={() => this.handleEditChange(note)}/>
                                                    </div>
                                                    <pre  className="post-text">{note.text}</pre> 
                                                </div>
                                        </Draggable>                              
                                })}
                                {this.state.edit
                                  ?<div style={{width: '300px', height: '300px'}}>
                                  <textarea className= "post" 
                                  style={{backgroundColor:`${currentNote.color}`, top:`${currentNote.y}px`,
                                  left: `${currentNote.x}px`}}
                                  value={editTyping}
                                  onChange={(e) => this.handleEditTypingChange(e)}
                                      >     
                                      <div className="btn" style={{fontSize:'24px', color:'white', textAlign:'right', paddingBottom: '20px'}}>     
                                      </div>
                                  </textarea>
                                    <RiDeleteBin6Line style={{top:`${currentNote.y}px`,
                                  left: `${currentNote.x}px`}} className="btn-cancel"  onClick={() => this.handleEditChange()} />
                                  </div>
                                  :null
                                }
                            </div>
                        </div>
                    :this.props.history.push('/')
                }
            </div>
        )
    }
}
