import { Image, message, Select  } from 'antd';
import { DeleteFilled, FolderAddFilled, CloseCircleFilled} from '@ant-design/icons';
import { useState } from 'react';
import PhotoList from './PhotoList';

const { Option } = Select;
function AlbumDetail(props) {

 const albumPhoto = props.photos.filter(photo => photo.album_id == props.album.id)

 const handleRemoveFromAblum =(albumObj) => {
      fetch(`http://localhost:3000/photoalbum`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          album_id: albumObj.id
        })
      })
      .then(res => {
          message.info("Photo removed from curren ablum")
          props.componentDidMount()
      })
    }

    return (
      <div className="photo-List">
            <Image.PreviewGroup>
                {albumPhoto.map(photo => {
                    return <PhotoList photo={photo} handleRemoveFromAblum={handleRemoveFromAblum}/> 
                })
                }
            </Image.PreviewGroup>

      </div>
    );
  }

  export default AlbumDetail;
