import React, { Component } from "react";
import _ from "lodash"

const sound = 'https://www.soundhelix.com/examples/mp3/SoundHelix-Song-1.mp3';


export default class Music extends Component {

    state = {
      isPause: false,
      musicList: [
        {
          id: "1",
          title: "打上花火",
          info: "DAOKO,米津玄師",
          resource: sound,
          time: "04:50",
          img:
            "http://singerimg.kugou.com/uploadpic/softhead/400/20160913/20160913140233132.jpg"
        },
      ],
      currentMusic: this.state.musicList ?  this.state.musicList[0] : {},
      totalTime: "00:00",
      currentTime: "00:00",
      processItemMove: false,
      volumeProcessItemMove: false,
      volumeControl: false,
      playMode: 1,
      isMusicListShow: false
     }


     static getDerivedStateFromProps(nextProps, prevState) {
      const { currentMusic, musicList: prevMusicList } = prevState;
      const { musicList = [] } = nextProps;
      // 判断音乐列表已经不同了
      if (!_.isEqual(musicList, prevMusicList)) {
        const oldIndex = prevMusicList.findIndex(item => {
          return currentMusic.id === item.id;
        });
        const hasCurrentMusic = musicList.findIndex(item => {
          return currentMusic.id === item.id;
        });
        let newCurrentMusic = musicList[oldIndex]
          ? musicList[oldIndex]
          : musicList[0];
        if (musicList.length === 0) {
          newCurrentMusic = currentMusic;
        }
        return {
          musicList,
          currentMusic: hasCurrentMusic === -1 ? newCurrentMusic : currentMusic
        };
      }
      return null;
    }



    render() {
      return (
        <div className="music">
            this is Music page
        </div>

      );
    }
  }
