import React, { Component } from "react";
import { Card, Upload, Modal, Form, Button, Input } from 'antd'
import { LoadingOutlined, PlusOutlined } from '@ant-design/icons';
import Navbar from './NavBar'
import PhotoDetial from './Container/PhotoDetail'


const FormItem = Form.Item;
const { TextArea } = Input;

function getBase64(file) {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = error => reject(error);
  });
}

export default class Photo extends Component {

  state = {
    value: '',
    previewVisible: false,
    previewImage: '',
    fileList:[],
    }

    onChange = ({ target: { value } }) => {
      this.setState({ value });
    };
    handleCancel = () => this.setState({ previewVisible: false });
    handlePreview = async file => {
      if (!file.url && !file.preview) {
        file.preview = await getBase64(file.originFileObj);
      }
      this.setState({
        previewImage: file.url || file.preview,
        previewVisible: true,
      });
      console.log(file);
    };
    handleChange = ({ fileList }) => this.setState({ fileList:fileList });
    beforeUpload=(file)=>{
        this.setState(({
          fileList: [this.state.fileList, file],
        }));
      return false;
    }

    handleSubmit = () => {
      fetch("http://localhost:3000/photoupload", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          url: this.state.fileList,
          user_id: this.props.loginUser.id
        }),
      })
      .then(res => res.json())
    }


    render() {
      const { previewVisible, previewImage, fileList,value} = this.state;

      const { getFieldDecorator } = this.props.form;
      const formItemLayout = {
        labelCol: { span: 8 },
        wrapperCol: { span: 10 },
      };
      const props={fileList};

      console.log(this.state.fileList);
      return (
      <div className="photo">
          <Navbar />
          <div className="photo-subnav">
              <li>Album</li>
              <li>Photos</li>
          </div>
          <div className="photo-btn">
             <button>Upload Photo</button>
             <button>Create Album</button>
          </div>

          <Form>

          </Form>

      </div>
      )
    }
  }


