import React, {Component} from "react";
import {CloseCircleOutlined } from '@ant-design/icons';

export default class Photo3D extends Component {
    state = {
        lastX: "" , 
        lastY: "" ,
        nowX: "", 
        nowY: "", 
        minX: "", 
        minY: "", 
        roX: "",
        roY: "" 
    }

    handleMouseClick = (e) => {
        this.setState({
            lastX: e.clientX,
            lastY: e.clientY
        })

        console.log(this.state.lastX)
     }

     handleOnMouseMove = (e) => {
         const {nowX, nowY, lastX, lastY, minX, minY, roX, roY} = this.state
         this.setState({
            nowX: e.clientX,
            nowY: e.clientY,
            minX: (nowX - lastX) ,
            minY: (nowY - lastY) ,
            roX:   minY * 0.1, 
            roY:   minX * 0.2,
            lastY: nowY,
            lastX: nowX
         })
     } 

     handleonMouseUp = (e) => {
        console.log(e)
     }

     timer = () => {
        setInterval(() => {
            this.setState({
                minX: this.state.minX * 0.95,
                minY: this.state.minY * 0.95
            })
         }, 1000 / 60)
        }

    render() {
        const length = this.props.photos.length;
        const deg = 360 / length
        const {roX, roY} = this.state
        return(
            <div className="view-card-page" >
              <CloseCircleOutlined style={{fontSize: '30px'}} onMouseDown={() => this.props.handleVeiew()}/>
              <div className="view-card" onMouseDown={(e) => this.handleMouseClick(e)} onMouseMove={(e) => this.handleOnMouseMove(e)}  onMouseUp={(e) => this.handleonMouseUp(e)}  style={{transform: `rotateX(${roX}deg) rotateY(${-roY}deg)` , transition: '1s'}}>
              {
                this.props.photos.map((photo, index) => {
                    return<img className="view-card-photo" src={photo.image} style={{transform: `rotateY(${index * deg}deg) translateZ(350px)`, transition: `1s ${(length - 1 - index) * 0.1}s`}} draggable= "false"
                    key={index}  
                    />
                  })
              }
               </div>
            </div>
        )
    }
}
