import React, { Component } from "react";
import { Card, Upload } from 'antd'
import { LoadingOutlined, PlusOutlined } from '@ant-design/icons';
import Navbar from './NavBar'
import PhotoDetial from './Container/PhotoDetail'


const SERVER_URL = 'http://localhost:3000'
const UPLOAD_URL = `${SERVER_URL}/upload`
export default class Photo extends Component {

    state = {
      previewVisible: false,
      previewImage: '',
      previewImageName: ''
    }

    hidePreview = () => {
      this.setState({
        previewVisible: false
      })
    }

    showPreview = file => {
      this.setState({
        previewVisible: true,
        previewImage: file.url || file.thumbUrl,
        previewImageName: file.name
      })
    }

    render() {
      const { previewVisible, previewImage, previewImageName } = this.state
    return (
      <div className="photo">
          <Navbar />
          <div className="photo-subnav">
              <li>Album</li>
              <li>Photos</li>
          </div>
          <div className="photo-btn">
             <button>Upload Photo</button>
             <button>Create Album</button>
             <button>3D view</button>
          </div>
          <Card title='图片上传'>
        <Upload
          listType='picture-card'
          action={UPLOAD_URL}
          onPreview={this.showPreview}
        >
          <PlusOutlined />
          <div style={{ marginTop: 8, color: '#666' }}>上传图片</div>
        </Upload>
        {/* 用于查看图片的 Modal 对话框 */}
        <Modal
          visible={previewVisible}
          footer={null}
          onCancel={this.hidePreview}
        >
          <img
            src={previewImage}
            alt={previewImageName}
            style={{ width: '100%' }}
          />
        </Modal>
      </Card>
      </div>

      )
    }
  }
