import React, { Component } from "react";
import { Card, Upload, Modal, Form, Button } from 'antd'
import { LoadingOutlined, PlusOutlined } from '@ant-design/icons';
import Navbar from './NavBar'
import PhotoDetial from './Container/PhotoDetail'

const FormItem = Form.Item;

function getBase64(file) {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = error => reject(error);
  });
}
export default class Photo extends Component {

    state = {
      previewVisible: false,
      previewImage: '',
      previewImageName: '',
      value: '',
      fileList: []
    }

    onChange = ({ target: { value } }) => {
      this.setState({ value });
    };

    handleChange = ({ fileList }) => {
      this.setState({ fileList:fileList })
    }

    hidePreview = () => {
      this.setState({
        previewVisible: false
      })
    }

    showPreview = file => {
      this.setState({
        previewVisible: true,
        previewImage: file.url || file.thumbUrl,
        previewImageName: file.name
      })
    }

    handlePreview = async file => {
      if (!file.url && !file.preview) {
        file.preview = await getBase64(file.originFileObj);
      }
      this.setState({
        previewImage: file.url || file.preview,
        previewVisible: true,
      });
      console.log(file);
    };





    handleSubmit = () => {
      fetch("http://localhost:3000/photoupload", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          url: this.state.fileList,
          user_id: this.props.loginUser.id
        }),
      })
      .then(res => res.json())
    }


    render() {
      const { previewVisible, previewImage, previewImageName, fileList} = this.state
      console.log(this.state.fileList);
    return (
      <div className="photo">
          <Navbar />
          <div className="photo-subnav">
              <li>Album</li>
              <li>Photos</li>
          </div>
          <div className="photo-btn">
             <button>Upload Photo</button>
             <button>Create Album</button>
             <button>3D view</button>
          </div>
        <Card title='Photo Upload'>
                <Upload
                  listType='picture-card'
                  action='http://localhost:3000/photo'
                  onPreview={this.showPreview}
                  onChange={this.handleChange}
                >
                  <PlusOutlined />
                  <div style={{ marginTop: 8, color: '#666' }}>Upload</div>
                </Upload>
                <Modal
                  visible={previewVisible}
                  footer={null}
                  onCancel={this.hidePreview}
                >
                  <img
                    src={previewImage}
                    alt={previewImageName}
                    style={{ width: '100%', height: '100%'}}
                  />
                </Modal>
            <Button onClick={() => this.handleSubmit()} type='primary' >submit</Button>
        </Card>
      </div>
      )
    }
  }
